package main

import "fmt"

const (
	testInput0 = "mjqjpqmgbljsphdztnvjfqwrcgsmlb"
)

func main() {
	fmt.Printf("first: %d\n", solve(Input, 4))
	fmt.Printf("first: %d\n", solve(Input, 14))
}

func solve(input string, size int) int {
	for i := 0; i < len(input)-size; i++ {
		characters := input[i : i+size]

		m := make(map[byte]any, size)
		for _, c := range characters {
			m[byte(c)] = true
		}

		if len(m) == size {
			return i + size
		}
	}

	return -1
}

